import{_ as a,o as p,c as e,a as l}from"./app.f0aed396.js";const s={},i=l('<h1 id="faasjs-vs-mvc" tabindex="-1"><a class="header-anchor" href="#faasjs-vs-mvc" aria-hidden="true">#</a> FaasJS vs MVC</h1><p><em>\u53D1\u5E03\u4E8E 2019-10-24</em></p><p>\u4ECA\u5929\u662F 10.24\uFF0C\u9996\u5148\u795D\u5927\u5BB6\u8282\u65E5\u5FEB\u4E50\uFF0C\u6CA1\u6709 BUG\uFF0C\u65E9\u65E9\u4E0B\u73ED\u56FE\u7247</p><p>\u4ECA\u5929\u60F3\u8DDF\u5927\u5BB6\u8BB2\u4E00\u4E0B FaasJS \u80CC\u540E\u7684\u8BBE\u8BA1\u7406\u5FF5\uFF1A</p><p><strong>\u4F7F\u7528 Function + Folder \u6765\u9AD8\u6548\u6784\u5EFA\u548C\u8FED\u4EE3\u590D\u6742\u7684\u4E1A\u52A1\u5E94\u7528\u3002</strong></p><p>\u4F5C\u4E3A\u5F00\u53D1\u4EBA\u5458\uFF0C\u9700\u8981\u9762\u5BF9\u4E24\u79CD\u590D\u6742\u6027\uFF1A\u4E1A\u52A1\u590D\u6742\u6027\u548C\u89C4\u6A21\u590D\u6742\u6027\u3002</p><p>\u800C\u89E3\u51B3\u590D\u6742\u6027\uFF0C\u6700\u597D\u7684\u529E\u6CD5\u5C31\u662F\u62C6\u5206\u3002</p><p>MVC \u6846\u67B6\u7684\u505A\u6CD5\u662F\u5C06\u4E00\u4E2A\u5355\u4F53\u5E94\u7528\u62C6\u5206\u4E3A\u591A\u4E2A\u5355\u4F53\u5E94\u7528\uFF0C\u7136\u540E\u901A\u8FC7\u5BB9\u5668\u96C6\u7FA4\u6765\u63D0\u4F9B\u670D\u52A1\uFF0C\u4EE5\u6B64\u6765\u62C6\u5206\u548C\u89E3\u8026\u4E1A\u52A1\u548C\u89C4\u6A21\uFF0C\u63D0\u5347\u541E\u5410\u91CF\u548C\u7A33\u5B9A\u6027\u3002</p><p>\u4F46\u8FD9\u79CD\u65B9\u5F0F\u6709\u4E09\u4E2A\u7F3A\u70B9\uFF1A</p><ol><li>\u89C4\u5212\u5355\u4F53\u5E94\u7528\u7684\u62C6\u5206\u9700\u8981\u5BF9\u4E1A\u52A1\u67B6\u6784\u548C\u6280\u672F\u67B6\u6784\u90FD\u6709\u8F83\u6DF1\u7684\u7406\u89E3\uFF0C\u4E14\u5DE5\u4F5C\u91CF\u548C\u5DE5\u4F5C\u96BE\u5EA6\u90FD\u4E0D\u4F4E\uFF1B</li><li>\u5355\u4F53\u5E94\u7528\u7684\u9AD8\u5185\u805A\u7279\u6027\uFF0C\u4F1A\u5BFC\u81F4\u968F\u7740\u4E1A\u52A1\u589E\u957F\uFF0C\u518D\u6B21\u9762\u4E34\u9700\u8981\u62C6\u5206\u7684\u60C5\u51B5\uFF1B</li><li>\u968F\u7740\u5E94\u7528\u6570\u91CF\u7684\u589E\u591A\uFF0C\u5F00\u53D1\u73AF\u5883\u548C\u6D4B\u8BD5\u73AF\u5883\u6240\u9700\u7684\u4F9D\u8D56\u5173\u7CFB\u53D8\u5F97\u8D8A\u53D1\u9519\u7EFC\u590D\u6742\uFF0C\u90E8\u7F72\u548C\u8FD0\u7EF4\u7684\u96BE\u5EA6\u4E5F\u8D8A\u6765\u8D8A\u5927\u3002</li></ol><p>\u7B80\u5355\u6765\u8BF4\uFF0C\u4EE5\u62C6\u5206\u5E94\u7528\u7684\u65B9\u5F0F\u6765\u89E3\u51B3\u590D\u6742\u6027\u95EE\u9898\uFF0C\u5176\u5B9E\u662F\u628A\u590D\u6742\u6027\u8F6C\u79FB\u5230\u4E86\u8FD0\u7EF4\u5C42\u9762\uFF0C\u5E76\u4E14\u4F1A\u5BFC\u81F4\u8FD0\u7EF4\u5C42\u9762\u7684\u590D\u6742\u6027\u6307\u6570\u4E0A\u5347\u3002</p><p>\u90A3 FaasJS \u662F\u5982\u4F55\u964D\u4F4E\u8FD9\u79CD\u590D\u6742\u6027\u6307\u6570\u4E0A\u5347\u7684\u60C5\u51B5\u7684\u5462\uFF1F</p><p>\u9996\u5148\u5728\u8FD0\u7EF4\u5C42\u9762\uFF0CFaasJS \u7684\u90E8\u7F72\u5BF9\u8C61\u662F FaaS \u96C6\u7FA4\uFF0C\u5176\u57FA\u672C\u5355\u4F4D\u662F\u51FD\u6570\uFF0C\u8FD9\u4F1A\u5E26\u6765\u4EE5\u4E0B\u597D\u5904\uFF1A</p><ul><li>\u539F\u5B50\u5316\u90E8\u7F72\uFF1A\u6309\u9700\u90E8\u7F72\uFF0C\u5FEB\u901F\u90E8\u7F72\u3002</li><li>\u539F\u5B50\u5316\u9694\u79BB\uFF1A\u6BCF\u4E2A\u51FD\u6570\u9501\u5B9A\u90E8\u7F72\u65F6\u7684\u4F9D\u8D56\u5173\u7CFB\uFF0C\u4E0D\u4F1A\u88AB\u52A8\u5347\u7EA7\u3002</li><li>\u539F\u5B50\u5316\u76D1\u63A7\uFF1A\u7EC6\u7C92\u5EA6\u7684\u4E86\u89E3\u6BCF\u4E2A\u51FD\u6570\u7684\u8FD0\u884C\u72B6\u6001\u548C\u5E76\u8FDB\u884C\u8C03\u63A7\u3002</li></ul><p>\u63A5\u4E0B\u6765\u5728\u5F00\u53D1\u5C42\u9762\uFF0CFaasJS \u4E3B\u8981\u4F7F\u7528\u4EE5\u4E0B\u65B9\u5F0F\u6765\u964D\u4F4E\u590D\u6742\u6027\uFF1A</p><ol><li>\u9762\u5411\u4E1A\u52A1\u6D41\u7A0B\u7F16\u7A0B\uFF0C\u7528\u51FD\u6570\u66FF\u4EE3\u7C7B\uFF1B</li><li>\u4EE5\u6587\u4EF6\u5939\u5F62\u5F0F\u76F4\u89C2\u7684\u9694\u79BB\u4E1A\u52A1\u9886\u57DF\uFF1B</li><li>\u4EE5\u51FD\u6570\u4E3A\u5355\u4F4D\u8FDB\u884C\u62C6\u5206\uFF0C\u4F7F\u5F97\u590D\u6742\u6027\u53EF\u4EE5\u4EE5\u8F83\u5E73\u6ED1\u7684\u7EBF\u6027\u6765\u589E\u957F\u3002</li></ol><p>MVC \u4E3A\u4E86\u5B9E\u73B0\u9AD8\u5185\u805A\uFF0C\u4F7F\u7528\u4E86\u5206\u5C42\u67B6\u6784\u548C\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\uFF0C\u4F46\u968F\u7740\u5C42\u7EA7\u8D8A\u6765\u8D8A\u591A\uFF0C\u7C7B\u7684\u7EE7\u627F\u5173\u7CFB\u8D8A\u6765\u8D8A\u590D\u6742\uFF0C\u590D\u6742\u5EA6\u5448\u6307\u6570\u4E0A\u5347\u7684\u8D8B\u52BF\u3002</p><p>FaasJS \u91C7\u7528\u9762\u5BF9\u4E1A\u52A1\u6D41\u7A0B\u7684\u51FD\u6570\u5F0F\u7F16\u7A0B\uFF0C\u5F7B\u5E95\u5C06\u6BCF\u4E2A\u4E1A\u52A1\u6D41\u7A0B\u63A7\u5236\u4E3A\u53EF\u9884\u6D4B\u7684\u8F93\u5165/\u8F93\u51FA\u51FD\u6570\uFF0C\u4EE5\u6B64\u6765\u63A7\u5236\u5F00\u53D1\u548C\u6D4B\u8BD5\u7684\u590D\u6742\u6027\u3002</p><p>\u540C\u65F6\uFF0C\u4F7F\u7528\u6587\u4EF6\u5939\u5F62\u5F0F\u6765\u5212\u5206\u4E91\u51FD\u6570\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u8BA9\u6574\u4F53\u67B6\u6784\u66F4\u76F4\u89C2\uFF0C\u800C\u4E14\u4F7F\u5F97\u62C6\u5206\u548C\u91CD\u6784\u66F4\u4E3A\u7075\u6D3B\u3002</p><p>\u5728 FaasJS \u4E2D\uFF0C\u6BCF\u4E2A\u6587\u4EF6\u5939\u90FD\u53EF\u4EE5\u8FDB\u884C\u72EC\u7ACB\u914D\u7F6E\uFF0C\u5B50\u6587\u4EF6\u5939\u53EF\u4EE5\u7EE7\u627F\u7236\u7CFB\u6587\u4EF6\u5939\u7684\u914D\u7F6E\uFF0C\u4E91\u51FD\u6570\u4EE3\u7801\u4E2D\u7684\u914D\u7F6E\u53EF\u4EE5\u8986\u76D6\u6587\u4EF6\u5939\u914D\u7F6E\u3002</p><p>\u4ECE\u800C\u5728\u914D\u7F6E\u5C42\u9762\u63D0\u4F9B\u4E86\u76F4\u89C2\u7684\u7EE7\u627F\u548C\u9694\u79BB\uFF0C\u5E76\u4E14\u968F\u7740\u4E91\u51FD\u6570\u90E8\u7F72\u5230\u7EBF\u4E0A\uFF0C\u914D\u7F6E\u6587\u4EF6\u4F1A\u9501\u5B9A\u5728\u90E8\u7F72\u65F6\u7684\u72B6\u6001\uFF0C\u4E0D\u7528\u62C5\u5FC3\u88AB\u7BE1\u6539\u3002</p><p>\u7EFC\u4E0A\u6240\u8FF0\uFF0CFaasJS \u57FA\u4E8E Serverless \u6280\u672F\uFF0C\u4F7F\u7528 Function + Folder \u6765\u5927\u5E45\u7B80\u5316\u4EE3\u7801\u7684\u8FED\u4EE3\u548C\u91CD\u6784\u6210\u672C\uFF0C\u4F7F\u5F97\u4E1A\u52A1\u53EF\u4EE5\u5FEB\u901F\u66F4\u65B0\uFF0C\u6240\u4EE5 F \u4E5F\u540C\u65F6\u4EE3\u8868\u4E86 Fast\uFF08\u867D\u7136\u5728\u6027\u80FD\u5C42\u9762\uFF0CServerless \u8BED\u5883\u4E0B\uFF0CNode.js \u7684\u6027\u80FD\u4F18\u52BF\u76F8\u5BF9\u4E0D\u90A3\u4E48\u7A81\u51FA\uFF09\u3002</p>',22),o=[i];function r(t,c){return p(),e("div",null,o)}var _=a(s,[["render",r],["__file","2019-10-24.html.vue"]]);export{_ as default};
